import React from "react";

const Alert = (props) => {
  return (
    props.alert && (
      <div
        class={`alert alert-${props.alert.typ} alert-dismissible fade show`}
        role="alert"
      >
        <strong>{props.alert.typ}</strong> :{props.alert.msg}
        <button
          type="button"
          class="btn-close"
          data-bs-dismiss="alert"
          aria-label="Close"
        ></button>
      </div>)
  );
};

export default Alert;
